import React from "react";
import AddNoteBtn from "./AddNoteBtn";
import ColorBtns from "./ColorBtns";

function Textarea() {
  return (
    <div>
      <form action="">
        <textarea
          placeholder="Enter your note here"
          className="textArea"
        ></textarea>
        <AddNoteBtn />
        <ColorBtns />
      </form>
    </div>
  );
}

export default Textarea;
