import { useState } from "react";
import { nanoid } from "@reduxjs/toolkit";
import { useDispatch } from "react-redux";
import { addNote } from "../redux/NoteReducer";

function Textarea() {
  const [note, setNote] = useState("");
  const dispatch = useDispatch();
  const colors = ["red", "blue", "yellow", "green", "gray"];

  const hanldeSubmit = (e) => {
    e.preventDefault();
    dispatch(addNote({ id: nanoid(), value: note, color: null }));
    setNote("");
  };
  return (
    <div>
      <form onSubmit={hanldeSubmit} className="form">
        <textarea
          placeholder="Enter your note here"
          className="textArea "
          value={note}
          onChange={(e) => setNote(e.target.value)}
        ></textarea>
        <div className="flex btnAndColors">
          <div className="colors flex m-2 pl-2">
            {colors.map((color) => (
              <button
                key={nanoid()}
                className="colorBox"
                value={color}
                type="button"
                style={{ backgroundColor: color, cursor: "pointer" }}
                onClick={hanldeSubmit}
              ></button>
            ))}
          </div>
          <button type="submit" className="bg-green-600 btn">
            Add
          </button>
        </div>
      </form>
    </div>
  );
}

export default Textarea;
