import { createSlice } from "@reduxjs/toolkit";

export const noteSlice = createSlice({
  name: "note",
  initialState: {
    notes: JSON.parse(localStorage.getItem("notes")) || [],
  },
  reducers: {
    addNote: (state, actions) => {
      const item = state.notes?.find(
        (note) => note.value === actions.payload.value
      );
      if (item) {
        return console.log("aynisindan var kardeş");
      } else {
        state.notes.push(actions.payload);
        localStorage.setItem("notes", JSON.stringify(state.notes));
      }
    },
    searchNote: (state, actions) => {
      const note = state.notes.filter(
        (item) => item.value.toLocaleLowerCase() === actions.payload
      );
      if (note) {
        return note;
      }
    },
  },
});

export const { addNote, searchNote } = noteSlice.actions;

export default noteSlice.reducer;
