import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
function EditScreen({ edit, setEdit }) {
  const dispatch = useDispatch();
  const item = useSelector((state) => state.notes.currentNote);
  const [currentNote, setCurrentNote] = useState(item.value);

  const saveSubmit = () => {
    dispatch(edit(editNote));
    setIsOpen(!isOpen);
    toast({
      description: "Note saved.",
      status: "success",
      duration: 3000,
      isClosable: true,
    });
  };

  const deleteSubmit = () => {
    dispatch(deleteNote());
    setIsOpen(!isOpen);
    toast({
      title: "Note deleted",
      duration: 3000,
      status: "warning",
      isClosable: true,
    });
  };

  return (
    <div className="editContainer">
      <div className="editContent">
        <div className="header">
          <h2>Note Edit</h2>
          <button className="closeBtn" onClick={() => setEdit(false)}>
            X
          </button>
        </div>
        <div className="editBody">
          <textarea
            className="editText"
            value={currentNote}
            onChange={(e) => setCurrentNote(e.target.value)}
          ></textarea>
        </div>
        <div className="subBtn">
          <button className="editDeleteBtn" onClick={() => deleteSubmit()}>
            Delete
          </button>
          <button className="editSaveBtn" onClick={() => saveSubmit()}>
            Save
          </button>
        </div>
      </div>
    </div>
  );
}

export default EditScreen;
