import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
function EditScreen({ edit, setEdit }) {
  const dispatch = useDispatch();
  const item = useSelector((state) => state.notes.currentNote);
  const [currentNote, setCurrentNote] = useState(item.value);
  return (
    <div className="editContainer">
      <div className="editContent">
        <div className="header">
          <h2>Note Edit</h2>
          <button className="closeBtn" onClick={() => setEdit(false)}>
            X
          </button>
        </div>
        <div className="editBody">
          <textarea className="editText"></textarea>
        </div>
        <div className="subBtn">
          <button className="editDeleteBtn">Delete</button>
          <button className="editSaveBtn">Save</button>
        </div>
      </div>
    </div>
  );
}

export default EditScreen;
