import React, { useState } from "react";

function ColorBtns() {
  const colors = ["red", "blue", "yellow", "green", "gray"];
  const [check, setCheck] = useState(false);
  return (
    <div>
      <ul className="colors flex m-2 pl-2">
        {colors.map((item, ind) => (
          <li
            key={ind}
            className={check ? "colorBox active" : "colorBox"}
            style={{ backgroundColor: item, cursor: "pointer" }}
            onClick={() => setCheck(!check)}
          ></li>
        ))}
      </ul>
    </div>
  );
}

export default ColorBtns;
