import React, { useState } from "react";

function ColorBtns() {
  const colors = ["red", "blue", "yellow", "green", "gray"];
  const [check, setCheck] = useState(false);
  return (
    <div>
      <ul className="colors flex m-2 pl-2">
        <li
          className={check ? "colorBox active" : "colorBox"}
          style={{ backgroundColor: "red", cursor: "pointer" }}
          onClick={() => setCheck(!check)}
        ></li>
        <li
          className={check ? "colorBox active" : "colorBox"}
          style={{ backgroundColor: "blue", cursor: "pointer" }}
          onClick={() => setCheck(!check)}
        ></li>
        <li
          className={check ? "colorBox active" : "colorBox"}
          style={{ backgroundColor: "yellow", cursor: "pointer" }}
          onClick={() => setCheck(!check)}
        ></li>
        <li
          className={check ? "colorBox active" : "colorBox"}
          style={{ backgroundColor: "green", cursor: "pointer" }}
          onClick={() => setCheck(!check)}
        ></li>
        <li
          className={check ? "colorBox active" : "colorBox"}
          style={{ backgroundColor: "gray", cursor: "pointer" }}
          onClick={() => setCheck(!check)}
        ></li>
      </ul>
    </div>
  );
}

export default ColorBtns;
